var _implements = require('../core/implements')
var _inspect = require('../core/inspect')
var type = require('../core/types').type('Sum')

var isNil = require('../core/isNil')
var isNumber = require('../core/isNumber')
var isSameType = require('../core/isSameType')

var _empty =
  function () { return Sum(0); }

function Sum(n) {
  var x = isNil(n) ? _empty().valueOf() : n

  if(!arguments.length || !isNumber(x)) {
    throw new TypeError('Sum: Numeric value required')
  }

  var valueOf =
    function () { return x; }

  var empty=
    _empty

  var inspect =
    function () { return ("Sum" + (_inspect(valueOf()))); }

  function concat(m) {
    if(!isSameType(Sum, m)) {
      throw new TypeError('Sum.concat: Sum required')
    }

    return Sum(x + m.valueOf())
  }

  return { inspect: inspect, valueOf: valueOf, type: type, concat: concat, empty: empty }
}

Sum['@@implements'] = _implements(
  [ 'concat', 'empty' ]
)

Sum.empty = _empty
Sum.type = type

module.exports = Sum
