var hasAlg = require('../core/hasAlg')
var isContravariant = require('../core/isContravariant')
var isFunctor = require('../core/isFunctor')

// isProfunctor : a -> Boolean
function isProfunctor(m) {
  return isFunctor(m)
    && isContravariant(m)
    && hasAlg('promap', m)
}

module.exports = isProfunctor
